import React from 'react';
import io from 'socket.io-client';
import { v1 as uuidv1 } from 'uuid';


class App extends React.Component {
  constructor() {
  super();
  this.state = {
    tasks: [{id:6565,taskName: "Go dsvrfereer"}],
  };
}
componentDidMount () {

  this.socket = io('localhost:8000');
  this.socket.on('updateData', (task) => this.updateTasks(task));
  this.socket.on('addTask', (tasks) => this.addTask(tasks));
//  this.socket.on('removeTask', (id) => this.removeTask(id));


}
removeTask(){

}
addTaskTask(task){
  const idTask = uuidv1();
  let newTask = {id: idTask, taskName: task};
  this.setState({
     tasks: [...this.state.tasks, newTask], //eslint-disable-line
   });
this.socket.emit('addTask', newTask)
}

updateTasks(task){
 this.setState({ tasks: task });
}

handleOnSubmit(event, task){
    event.preventDefault();
  if (!task) {alert('Wprowadź task!');}
  else
{this.addTaskTask(task);
 document.getElementById('task-name').value='';}
}
  render() {
    return (
    <div className="App">

      <header>
        <h1>ToDoList.app</h1>
      </header>

      <section className="tasks-section" id="tasks-section">
        <h2>Tasks</h2>
              <ul className="tasks-section__list" id="tasks-list">
          {this.state.tasks.map((itemTask)=>
            <li className="task" key={itemTask.id}>
              {itemTask.taskName}
                <button
                  type="button"
                  className="btn btn--red"
                  onClick={(e) => this.removeTask(itemTask.id, e)}
                >
                Remove
                </button>
            </li>
          )}
</ul>

        <form id="add-task-form" onSubmit={(event) => this.handleOnSubmit(event, document.getElementById('task-name').value)}>
          <input className="text-input"  type="text" placeholder="Type your description" id="task-name" />
          <button className="btn" type="submit">Add</button>

        </form>

      </section>
    </div>
  );

  };

};

export default App;
